mure:interactivity.script:

(function () {
  function load (url, callback) {
    var xhr;
    if (typeof XMLHttpRequest !== 'undefined') {
      xhr = new XMLHttpRequest();
    } else {
      var versions = [
        'MSXML2.XmlHttp.5.0',
        'MSXML2.XmlHttp.4.0',
        'MSXML2.XmlHttp.3.0',
        'MSXML2.XmlHttp.2.0',
        'Microsoft.XmlHttp'
      ];
      for (var i = 0, len = versions.length; i < len; i++) {
        try {
          xhr = new ActiveXObject(versions[i]);
          break;
        } catch (e) {}
      }
    }

    xhr.onreadystatechange = ensureReadiness;

    function ensureReadiness () {
      if (xhr.readyState < 4) {
        return;
      }

      if (xhr.status !== 200) {
        return;
      }

      // all is well
      if (xhr.readyState === 4) {
        callback(xhr.responseText);
      }
    }

    xhr.open('GET', url, true);
    xhr.send('');
  }

  var libraries = Array.from(document.getElementsByTagNameNS('http://mure-apps.github.io', 'library'))
    .map(function (libraryTag) { return libraryTag.getAttribute('src'); });
  var loadedLibraries = {};

  libraries.forEach(function (script) {
    loadedLibraries[script] = false;
    load(script, function (scriptText) {
      eval(scriptText);
      loadedLibraries[script] = true;
      attemptStart();
    });
  });

  function attemptStart() {
    var allLoaded = libraries.every(function (script) {
      return loadedLibraries[script];
    });
    if (allLoaded) {
      window.MURE_ON_LOAD();
    }
  }
})();


window.MURE_ON_LOAD = function () {
  var KEY = { w: 87, a: 65, s: 83, d: 68 };
  var moveSpeed = 10;
  var circle = d3.select('circle');
  var x = parseInt(circle.attr('cx'));
  var y = parseInt(circle.attr('cy'));
  document.documentElement.addEventListener('keydown', function (evt) {
    switch (evt.keyCode) {
      case KEY.w:
        circle.attr('cy', y -= moveSpeed);
        break;
      case KEY.s:
        circle.attr('cy', y += moveSpeed);
        break;
      case KEY.a:
        circle.attr('cx', x -= moveSpeed);
        break;
      case KEY.d:
        circle.attr('cx', x += moveSpeed);
        break;
    }
  }, false);
}
